function onCreate()	get = getRandomInt(1,4)	setTextFont('botplayTxt', 'smb1.ttf')	setTextSize('botplayTxt', 6*6, 6*6)	setProperty('botplayTxt.antialiasing', false)	setProperty('botplayTxt.x', 250)	setProperty('botplayTxt.y', 120)endfunction onUpdate()	if get == 1 then		setTextString("botplayTxt", "CHEATING DOESN'T\nGET YOU FAR")	elseif get == 2 then		setTextString("botplayTxt", "PUSSY")	elseif get == 3 then		setTextString("botplayTxt", "CAN'T PLAY THE SONG, LIL' BABY?")	elseif get == 4 then		setTextString("botplayTxt", "CHEATER")	elseif get == 5 then		setTextString("botplayTxt", "BOTPLAY")	endendfunction onEvent(n, v1, v2)	if n == 'SonicUI' then		if SonicUI == false then			SonicUI = true			setTextFont('botplayTxt', 'smb1.ttf')			setTextSize('botplayTxt', 6*6, 6*6)			setProperty('botplayTxt.antialiasing', false)			setProperty('botplayTxt.x', 250)			setProperty('botplayTxt.y', 120)		else			SonicUI = false			setTextFont('botplayTxt', 'sonicdebugfont.ttf')			setTextSize('botplayTxt', 3*7, 3*7)			setProperty('botplayTxt.antialiasing', false)			setProperty('botplayTxt.x', 250)			setProperty('botplayTxt.y', 120)		end	endend